mapper.select:
select student.name, score.*
from student
join score on score.student_id = student.student_id
where student.teacher_id = #{id}
and score.title = #{title}
order by score.create_time

    limit #{offset}, #{pageSize};

select



            ${subject}



from score
where student_id = #{id}
order by create_time desc
limit 5 ;
